con:config.script:
import com.eviware.soapui.support.XmlHolder

//Scope of context variable lies in test case level
context.setProperty("Name","Venkat")
log.info context.getProperty("Name") 

// To get custom property 
log.info context.expand('${#TestCase#Age}')
log.info context.expand('${#TestCase#Dept}')

// Get hold of response
def response= context.expand('${GetEmployeeDetails#response}')
log.info response

// Get the desired node value from response
def xml = new XmlHolder(response)
def derirednode = xml.getNodeValue("//ns:name")
log.info derirednode

// Get the request from delete employee
def request = context.expand('${DeleteEmployee#request}')
def xmlreq = new XmlHolder(request)

// Place the node value in delete employee
xmlreq.setNodeValue("//typ:employeeName",derirednode)
def dxml = xmlreq.getXml()

// Run the Delete Employee request
testRunner.testCase.testSteps["DeleteEmployee"].setPropertyValue("Request",dxml)

// Run the delete employee test
desiredtestStep = testRunner.testCase.testSteps["DeleteEmployee"]
desiredtestStep.run(testRunner,context)
